INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000053', 'Ai Yori Aoshi', '2002-04-11', 'Kaoru Hanabishi, a college student who lives alone, met a beautiful but bewildered girl dressed in a kimono at a train station. He volunteered to guide her way to the address she was looking for, which happened to be in his neighborhood, but turned out to be an empty lot. Not knowing what to do next, Kaoru invited the devastated girl to his apartment and asked for any additional clues to her destination. She supplied him with a photo of two children whom Kaoru immediately identified as himself and Aoi Sakuraba, his childhood friend. It turned out that the girl in front of him is Aoi Sakuraba herself, his betrothed fiancee who came all the way to Tokyo to marry him. Her revelation was not only surprising but also reminded the deepest part of Kaoru''s memory for why he left the Hanabishi family in the first place.', 'https://cdn.myanimelist.net/images/anime/7/10172.jpg', '7.10', 'f', '23 min per ep', 'Spring 2002', 456, 'Manga', 'TV', 'Japanese', 'https://img.youtube.com/vi/4wyQ6ZAojI8/maxresdefault.jpg', '藍より青し', '{"Bluer Than Indigo"}', 24, NULL, 'PG-13 - Teens 13 or older');
