INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000183', 'Whistle!', '2002-05-06', 'Kazamatsuri Shou''s dream has always been to become a professional soccer player, but he has one problem: he''s not very good at the game. He was accepted to the prestigious Musashi no Mori Junior High, known for its top rate soccer team, but he was never able to rise beyond the rank of third stringer. After transferring to Sakura Jousui Junior High, he can finally play soccer. And, with the support of his new friends and teammates, his strong determination, and lots of hard work, his soccer skills are developing rapidly and setting Shou well on his way to achieving his dream.

