INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000234', 'Dan Doh!!', '2004-04-03', 'Dandoh was playing a baseball game with friends Yuka and Kohei and hits a wild fly ball which crashes into the principal''s potted plants. Rather than getting mad, the principal admires Dandoh''s swing and introduces him to the world of golf. It''s now an adventure in golf as Dandoh is under the watchful eye of a former pro-golfer. They enter a tournament together and play for a shot at the national championships.

