INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000204', 'Yumeria', '2004-01-09', 'On his 16th birthday, Tomokazu Mikuri had a realistic dream where he sees a girl battling a giant floating monstrosity. When he wakes up, he is surprised that the girl is actually sleeping next to him... Whenever he sleeps from now on, he ends up back at the dream world, and more and more people that he knows keep showing up there too. He finds out from a mysterious masked woman in the dream world named Silk that they are fighting against one named Faydoom, and he is the one who provides the powers to those girls so that they can fight these monsters. And so it goes...

