INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000208', 'R.O.D: Read or Die', '2001-05-23', 'Yomiko Readman is a lovable, near-sighted bibliomaniac working as a substitute teacher at a Japanese high school. Her real identity, however, is that of a secret agent for the British Library Special Operations Division. Her codename: "The Paper." The moniker denotes her supernatural ability to freely manipulate paper into any object she can imagine, including tools and weapons in her fight against the powerful and self-serving IJIN (Great Historical Figure) Army! Along with her partner, the enigmatic "Ms. Deep," Yomiko travels across the world in attempt to solve the mystery behind the reincarnation of historical figures and their attempt to control the world.

