INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000336', 'Ginyuu Mokushiroku Meine Liebe', '2004-11-04', 'The first season begins with introducing the characters, their past and their ambitions. Orpherus, Eduard, Camus, Lui, and Naoji are five noblemen who attend the prestigious Rozenstolz academy, and who are a part of the Strahl class—a class for potential candidates for the advisory positions in the royal palace in the small European country of Kuchen. Isaac is an English writer who visits Kuchen. While still students, the five noblemen are forced to deal with corrupted politicians, secret agents, attempted murder and ambitious individuals from both inside and outside of school who threatens to break the fragile peace in their country, and even assassinate the king.', 'https://cdn.myanimelist.net/images/anime/11/75547.jpg', '6.42', 'f', '24 min per ep', 'Fall 2004', 30, 'Visual novel', 'TV', 'Japanese', NULL, '吟遊黙示録 マイネリーベ', '{"Meine Liebe","My Love"}', 13, NULL, 'PG-13 - Teens 13 or older');
