INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000395', 'Gantz 2nd Stage', '2004-08-26', 'Despite having seemingly returned to their normal lives, Kei Kurono, Masaru Katou, and Kei Kishimoto are forced back into the lethal game after Gantz''s recent activity. The black sphere continues to summon recently deceased people to fight alien invaders. And having survived the two previous encounters, the trio are more than prepared for the upcoming missions.

