INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000406', 'Buzzer Beater', '2005-02-05', 'Hideyoshi is a homeless boy living in New York in the near future, who survives by hustling other kids in basketball games. But now he''s been drafted into a pro team-a pro team with a very unique goal. There once was a time when humans dominated the game of basketball. But times have changed, and now the physically superior alien players have taken over the game, and there are barely any human players left. But Hideyoshi''s team-the first all-human team to compete in the Space League-intends to change all that, and bring the championship back home to Earth.

