INSERT INTO "anime_table" ("anime_id", "title", "release_date", "description", "cover_image", "average_rating", "ongoing_status", "runtime", "airing_season", "favorites", "source", "showtype", "language", "top_image", "title_japanese", "title_synonyms", "episode_no", "base_anime_id", "rated") VALUES ('A00000000000472', 'To Heart', '1999-04-02', 'For as long as Akari can remember, she and Hiroyuki have always been friends. But with time, everything changes, and her feelings have turned into something more. As a new semester of high school begins, will the two childhood friends come closer together or drift further apart? Join Hiroyuki, Akari and all their friends—the bubbly Shiho, the quiet Serika, the lovely Kotone, and more—in this heartwarming tale of love, relationships and friendship!

